    <button class="action-btn btn-go" id="m-btn">前往</button>
  </div>
</div>

<script>
  const modal = document.getElementById('modal');
  const overlay = document.getElementById('overlay');
  const mTitle = document.getElementById('m-title');
  const mDesc = document.getElementById('m-desc');
  const mBtn = document.getElementById('m-btn');

  // 打开地点弹窗
  function openLoc(name, desc) {
    mTitle.innerText = name;
    mDesc.innerText = desc;

    // 绑定点击事件，防止重复绑定
    mBtn.onclick = () => triggerSend(name);

    modal.style.display = 'block';
    overlay.style.display = 'block';
  }

  // 关闭
  function closeLoc() {
    modal.style.display = 'none';
    overlay.style.display = 'none';
  }

  // 发送指令
  function triggerSend(target) {
    const content = `/send {{user}}前往${target}|/trigger`;

    // 尝试调用酒馆内置/插件函数
    if (window.triggerSlash) {
        window.triggerSlash(content);
    } else {
        console.warn('未检测到 triggerSlash，模拟发送:', content);
        // 如果是在不支持脚本的环境，可以做个简单的alert提示
        // alert('指令已生成: ' + content); 
    }

    closeLoc();
  }
</script>

</body>
</html>
