    .entity { position: absolute; image-rendering: pixelated; pointer-events: auto; }

    .plant { width: 80px; height: 80px; left: 10px; top: 20px; z-index: 20; }
    .zombie { width: 90px; height: 90px; top: 15px; z-index: 30; transform: scaleX(-1); transition: filter 0.1s; }

    .bullet { 
