                    grid.querySelectorAll('.opt-btn').forEach(b => b.classList.remove('selected'));
                    btn.classList.add('selected');
                    current[key] = val;
                };
                grid.appendChild(btn);
            });
            current[key] = '随机'; // 默认随机
        }

        function goConfig(mode) {
            current.mode = mode;
            document.getElementById('partner-box').style.display = (mode === 'multi') ? 'block' : 'none';
            initUI('grid-id', worldData.ids, 'id');
            initUI('grid-bg', worldData.bgs, 'bg');
            initUI('grid-lv', worldData.lvs, 'lv');
            if(mode === 'multi') initUI('grid-pt', worldData.pts, 'pt');
            switchScene('scene-config');
        }

        function generateDoc() {
            // 优先读取自定义文本
            const cId = document.getElementById('custom-id').value;
            const cBg = document.getElementById('custom-bg').value;
            const cLv = document.getElementById('custom-lv').value;
            const cPt = document.getElementById('custom-pt').value;

            const final = {
                id: cId || (current.id === '随机' ? worldData.ids[Math.floor(Math.random()*7)] : current.id),
                bg: cBg || (current.bg === '随机' ? worldData.bgs[Math.floor(Math.random()*7)] : current.bg),
                lv: cLv || (current.lv === '随机' ? worldData.lvs[Math.floor(Math.random()*6)] : current.lv),
                pt: current.mode === 'single' ? '孤身一人' : (cPt || (current.pt === '随机' ? worldData.pts[Math.floor(Math.random()*4)].n : current.pt))
            };

            current.final = final;

            // 爆闪与转场
            const fLayer = document.getElementById('flash-overlay');
            const fImg = document.getElementById('flash-img');
            fImg.src = current.mode === 'multi' ? (worldData.pts.find(x=>x.n===final.pt)?.i || worldData.pts[0].i) : "https://files.catbox.moe/o1mwtc.PNG";

            fLayer.style.display = 'flex';
            setTimeout(() => {
                fLayer.style.display = 'none';
                renderPaper(final);
                switchScene('scene-exam-info');
            }, 600);
        }

        function renderPaper(f) {
            document.getElementById('info-display').innerHTML = `
                <div class="info-line"><b>姓名：</b> {{user}}</div>
                <div class="info-line"><b>试炼背景：</b> ${f.id}</div>
                <div class="info-line"><b>考场场域：</b> ${f.bg}</div>
                <div class="info-line"><b>危险系数：</b> <span style="color:var(--blood)">${f.lv}</span></div>
                <div class="info-line"><b>同行伴考：</b> ${f.pt}</div>
            `;
        }

        function switchScene(id) {
            document.querySelectorAll('.scene').forEach(s => s.classList.remove('active-scene'));
            document.getElementById(id).classList.add('active-scene');
            window.scrollTo(0, 0);
        }

        function resetAll() {
            document.querySelectorAll('.custom-input').forEach(i => i.value = '');
            switchScene('scene-start');
        }

        function startST() {
            const f = current.final;
            const cmd = `/send {{user}}进入了试炼考试，考生身份背景为【${f.id}】，试炼场域设定在【${f.bg}】，危险等级判定为【${f.lv}】，同行伴考人员：【${f.pt}】。|/trigger`;

            if (typeof triggerSlash === 'function') {
                triggerSlash(cmd);
            } else {
                console.log("模拟指令发送: " + cmd);
            }
            alert("因果已成，试炼开始。");
        }
    </script>
</body>
</html>
