        <div class="battle-field" id="battle-field"></div>

        <div id="tooltip"></div>

        <div id="start-screen" class="modal-overlay">
            <div class="modal-content">
                <h1 class="modal-title">寂川高校保卫战</h1>
                <p style="margin-bottom:20px; color:#aaa; font-size:1.2em;">点击下方按钮开启试炼<br>手机端可点击卡片后再点击草地放置</p>
                <button class="btn-start" onclick="startGame()">开始游戏</button>
            </div>
        </div>

        <div id="win-modal" class="modal-overlay" style="display:none">
            <div class="modal-content">
                <h1 class="modal-title" style="color: #ffd700;">修炼成功</h1>
                <p>道心稳固，邪祟尽除！</p>
                <div class="btn-group">
                    <button class="btn-retry" onclick="restartGame()">再次修炼</button>
                    <button class="btn-action" onclick="sendResult('win')">休息</button>
                </div>
            </div>
        </div>

        <div id="lose-modal" class="modal-overlay" style="display:none">
            <div class="modal-content">
                <h1 class="modal-title" style="color: #ff4d4d;">试炼失败</h1>
                <p>僵尸突破了防线...</p>
                <div class="btn-group">
                    <button class="btn-retry" onclick="restartGame()">再次试炼</button>
                    <button class="btn-action" onclick="sendResult('lose')">暂时放弃</button>
                </div>
            </div>
        </div>
    </div>
</div>

<script>
/**
 * 配置
 */
const CONFIG = {
    totalTime: 180, 
    rows: 5,
    cols: 9,
    bulletImg: 'https://files.catbox.moe/o1mwtc.PNG',
    sfxShoot: 'https://files.catbox.moe/mnqjye.mp3',
    sfxBoom: 'https://files.catbox.moe/5j41dw.mp3'
};

const UNIT_STATS = {
    'qian': { name:'钱兜兜', hp: 200, cost: 50, dmg: 0, speed: 0, text: '战斗力: E\n每5秒产出50灵知', img: 'https://files.catbox.moe/7tsdvw.png' },
    'lan': { name:'蓝不录', hp: 300, cost: 100, dmg: 15, speed: 2000, text: '战斗力: C\n符箓射击 (攻速中)', img: 'https://files.catbox.moe/255dch.png' },
    'mingye': { name:'明夜', hp: 1500, cost: 50, dmg: 0, speed: 0, text: '战斗力: B\n高防御肉盾', img: 'https://files.catbox.moe/ub5q55.png' },
    'ming': { name:'明离书', hp: 300, cost: 200, dmg: 35, speed: 800, text: '战斗力: S\n极速高伤符箓 (攻速快)', img: 'https://files.catbox.moe/64ueh1.png' },
    'ling': { name:'聆声', hp: 300, cost: 150, dmg: 999, speed: 0, text: '战斗力: A\n范围爆炸 (一次性)', img: 'https://files.catbox.moe/bfh5m3.png' },
    // 难度调整：僵尸HP增加
    'z_normal': { name:'游魂小鬼', hp: 150, dmg: 30, text: 'HP: 150\n群体出没', img: 'https://files.catbox.moe/b9zxhw.PNG' },
    'z_bucket': { name:'怨灵刃', hp: 700, dmg: 60, text: 'HP: 600\n极度耐打', img: 'https://files.catbox.moe/ml8r5a.png' }
};

let state = {
    lingzhi: 150,
    time: CONFIG.totalTime,
    running: false,
    plants: [],
    zombies: [],
    bullets: [],
    lastTime: 0,
    selectedCard: null // 当前选中的卡片(用于手机点击放置)
};

const fieldEl = document.getElementById('battle-field');
const tipEl = document.getElementById('tooltip');
const bgm = document.getElementById('bgm');
const gameContainer = document.getElementById('game-container');
const scalerWrapper = document.getElementById('scaler-wrapper');

/* ================= 自适应缩放逻辑 (SillyTavern适配) ================= */
function resizeGame() {
    // 获取父级宽度（SillyTavern的消息气泡宽度）
    // 如果在iframe中，则是window.innerWidth
    const parentWidth = scalerWrapper.parentElement ? scalerWrapper.parentElement.clientWidth : window.innerWidth;

    // 计算缩放比例，预留一点边距
    // 900px是游戏的原始宽度
    let scale = Math.min(1, (parentWidth - 20) / 900);

    // 应用缩放
    gameContainer.style.transform = `scale(${scale})`;
    gameContainer.style.transformOrigin = `top left`;

    // 调整包裹层高度，防止下方内容被遮挡或留白太多
    // 原始高度700px * 缩放比例
    scalerWrapper.style.height = `${700 * scale}px`;
    scalerWrapper.style.width = `${900 * scale}px`;
}

// 监听窗口大小变化
window.addEventListener('resize', resizeGame);
// 初始化时调用一次
setTimeout(resizeGame, 100);

/* ================= 初始化 ================= */
function init() {
    // 生成网格
    for(let r=0; r<CONFIG.rows; r++) {
        let row = document.createElement('div');
        row.className = 'field-row';
        row.id = `row-${r}`;
        for(let c=0; c<CONFIG.cols; c++) {
            let cell = document.createElement('div');
            cell.className = 'grid-cell';
            cell.dataset.r = r;
            cell.dataset.c = c;

            // 拖拽事件
            cell.ondragover = e => e.preventDefault();
            cell.ondrop = handleDrop;
            cell.ondragenter = e => e.target.classList.add('drag-over');
            cell.ondragleave = e => e.target.classList.remove('drag-over');

            // 点击事件 (用于手机端点选放置)
            cell.onclick = () => handleClickPlace(cell);

            row.appendChild(cell);
        }
        fieldEl.appendChild(row);
    }
}

function startGame() {
    document.getElementById('start-screen').style.display = 'none';
    state.running = true;
    bgm.volume = 0.3;
    bgm.play().catch(e => console.log("Audio play blocked", e));

    requestAnimationFrame(gameLoop);
    setInterval(updateTimer, 1000);
    startZombieWaves();
    resizeGame(); // 确保开始时缩放正确
}

function restartGame() {
    state.lingzhi = 150;
    state.time = CONFIG.totalTime;
    state.running = true;
    state.plants = [];
    state.zombies = [];
    state.bullets = [];
    state.selectedCard = null;
    clearSelection();

    document.querySelectorAll('.entity').forEach(el => el.remove());
    document.getElementById('win-modal').style.display = 'none';
    document.getElementById('lose-modal').style.display = 'none';

    updateUI();
    document.getElementById('time-val').innerText = "03:00";

    bgm.currentTime = 0;
    bgm.play().catch(()=>{});
}

function playSfx(url) {
    let audio = new Audio(url);
    audio.volume = 0.4;
    audio.play().catch(()=>{});
}

/* ================= 交互逻辑 (拖拽 + 点击) ================= */
// 选中卡片 (点击模式)
function selectCard(cardEl) {
    if(!state.running) return;
    const type = cardEl.dataset.type;
    const info = UNIT_STATS[type];

    // 如果钱不够，无法选中
    if(state.lingzhi < info.cost) return;

    // 切换选中状态
    if(state.selectedCard === type) {
        clearSelection(); // 取消选中
    } else {
        clearSelection();
        state.selectedCard = type;
        cardEl.classList.add('selected');
        // 高亮所有空格子提示可放置
        document.querySelectorAll('.grid-cell').forEach(cell => {
            if(cell.childElementCount === 0) cell.classList.add('highlight');
        });
    }
}

function clearSelection() {
    state.selectedCard = null;
    document.querySelectorAll('.card').forEach(c => c.classList.remove('selected'));
    document.querySelectorAll('.grid-cell').forEach(c => c.classList.remove('highlight'));
}

// 拖拽开始
function handleDragStart(e) {
    if(!state.running) return;
    // 拖拽时取消点击选中状态，避免冲突
    clearSelection();
    e.dataTransfer.setData('type', e.target.dataset.type);
    hideTip();
}

// 点击放置 (手机/鼠标)
function handleClickPlace(cell) {
    if(!state.selectedCard) return;
    if(cell.childElementCount > 0) return; // 已有植物

    placePlant(state.selectedCard, cell);

    // 放置后，不仅扣费还要检查是否还能买得起同类，如果能，保持选中方便连续放置？
    // 为了防止误触，手机端通常放置一次后取消选中较好
    // 如果你想连续放置，可以注释掉下面这行
    clearSelection(); 
}

// 拖拽放置
function handleDrop(e) {
    e.preventDefault();
    e.target.classList.remove('drag-over');
    if(!state.running || !e.target.classList.contains('grid-cell') || e.target.childElementCount > 0) return;

    let type = e.dataTransfer.getData('type');
    placePlant(type, e.target);
}

// 核心放置逻辑
function placePlant(type, cell) {
    let info = UNIT_STATS[type];

    if(state.lingzhi < info.cost) {
        showFloatText(cell, "灵知不足!", "red");
        clearSelection();
        return;
    }

    state.lingzhi -= info.cost;
    updateUI();

    let r = parseInt(cell.dataset.r);
    let c = parseInt(cell.dataset.c);

    let img = document.createElement('img');
    img.src = info.img;
    img.className = 'entity plant';
    // 赋予点击查看属性功能
    img.onclick = (ev) => { ev.stopPropagation(); showTip(ev, type); };

    cell.appendChild(img);

    if(type === 'ling') {
        setTimeout(() => {
            explode(r, c);
            img.remove();
        }, 1000);
    } else {
        state.plants.push({
            id: Math.random(),
            type: type,
            r: r, c: c,
            hp: info.hp,
            maxHp: info.hp,
            timer: 0,
            el: img
        });
    }
}

/* ================= 游戏循环与逻辑 ================= */
// 生成僵尸
function spawnZombie(type) {
    let r = Math.floor(Math.random() * CONFIG.rows);
    let info = UNIT_STATS[type];
    let rowEl = document.getElementById(`row-${r}`);

    let img = document.createElement('img');
    img.src = info.img;
    img.className = 'entity zombie';
    img.style.left = '900px';

    rowEl.appendChild(img);

    state.zombies.push({
        id: Math.random(),
        type: type,
        r: r,
        x: 900,
        hp: info.hp,
        maxHp: info.hp,
        dmg: info.dmg,
        el: img,
        state: 'walk',
        target: null
    });
}

// 游戏主循环
function gameLoop(time) {
    if(!state.running) {
        state.lastTime = time; 
        requestAnimationFrame(gameLoop);
        return;
    }

    let dt = time - state.lastTime;
    state.lastTime = time;

    // 植物
    state.plants.forEach((p, idx) => {
        p.timer += dt;
        let stats = UNIT_STATS[p.type];

        if(p.type === 'qian') {
            if(p.timer > 5000) {
                p.timer = 0;
                state.lingzhi += 50;
                updateUI();
                showFloatText(p.el.parentElement, "+50", "#ffd700");
            }
        }
        else if(stats.speed > 0) {
            let hasEnemy = state.zombies.some(z => z.r === p.r && z.x > p.c*100 && z.x < 900);
            if(hasEnemy && p.timer > stats.speed) {
                p.timer = 0;
                playSfx(CONFIG.sfxShoot);
                createBullet(p.r, p.c, stats.dmg);
            }
        }

        if(p.hp <= 0) {
            p.el.remove();
            state.plants.splice(idx, 1);
        }
    });

    // 僵尸 (速度微调：0.04 -> 0.05)
    state.zombies.forEach((z, idx) => {
        if(z.state === 'walk') {
            z.x -= 0.05 * dt; 
            z.el.style.left = z.x + 'px';

            if(z.x < -50) gameOver(false);

            let zLeft = z.x + 20;
            let col = Math.floor(zLeft / 100);
            let plant = state.plants.find(p => p.r === z.r && p.c === col);

            if(plant && Math.abs(zLeft - col*100) < 60) {
                z.state = 'eat';
                z.target = plant;
                z.el.style.animation = 'shake 0.2s infinite';
            }
        } 
        else if(z.state === 'eat') {
            if(z.target && z.target.hp > 0) {
                z.target.hp -= 0.8;
            } else {
                z.state = 'walk';
                z.target = null;
                z.el.style.animation = '';
            }
        }

        if(z.hp <= 0) {
            z.el.remove();
            state.zombies.splice(idx, 1);
            if(state.time <= 0 && state.zombies.length === 0) gameOver(true);
        }
    });

    // 子弹
    state.bullets.forEach((b, idx) => {
        b.x += 0.5 * dt;
        b.el.style.left = b.x + 'px';

        let hitZombie = state.zombies.find(z => z.r === b.r && b.x > z.x+20 && b.x < z.x+70);
        if(hitZombie) {
            hitZombie.hp -= b.dmg;
            hitZombie.el.style.filter = 'brightness(2) hue-rotate(90deg)';
            setTimeout(()=> { if(hitZombie.el) hitZombie.el.style.filter=''; }, 100);

            b.el.remove();
            state.bullets.splice(idx, 1);
        }
        else if(b.x > 900) {
            b.el.remove();
            state.bullets.splice(idx, 1);
        }
    });

    requestAnimationFrame(gameLoop);
}

function createBullet(r, c, dmg) {
    let rowEl = document.getElementById(`row-${r}`);
    let img = document.createElement('img');
    img.src = CONFIG.bulletImg;
    img.className = 'entity bullet';
    let startX = c * 100 + 60;
    img.style.left = startX + 'px';
    rowEl.appendChild(img);

    state.bullets.push({ r: r, x: startX, dmg: dmg, el: img });
}

function explode(r, c) {
    playSfx(CONFIG.sfxBoom);
    state.zombies.forEach(z => {
        let zCol = Math.floor(z.x / 100);
        if(Math.abs(z.r - r) <= 1 && Math.abs(zCol - c) <= 1) {
            z.hp -= 9999;
        }
    });
    let cell = document.querySelector(`.grid-cell[data-r="${r}"][data-c="${c}"]`);
    if(cell) {
        let boom = document.createElement('div');
        boom.style.cssText = "position:absolute;width:200px;height:200px;background:rgba(255,0,0,0.6);border-radius:50%;transform:translate(-50px,-50px);pointer-events:none;box-shadow:0 0 20px red;";
        cell.appendChild(boom);
        setTimeout(()=>boom.remove(), 300);
    }
}

function updateTimer() {
    if(!state.running) return;
    state.time--;
    let m = Math.floor(state.time / 60);
    let s = (state.time % 60).toString().padStart(2, '0');
    document.getElementById('time-val').innerText = `0${m}:${s}`;

    if(state.time <= 0 && state.zombies.length === 0) gameOver(true);
}

function startZombieWaves() {
    // 增加难度：更多的重叠僵尸
    let waveSchedule = [
        {t: 175, count: 1, type: 'z_normal'},
        {t: 165, count: 1, type: 'z_normal'},
        {t: 155, count: 2, type: 'z_normal'}, 
        {t: 140, count: 3, type: 'z_normal'}, 
        {t: 135, count: 1, type: 'z_bucket'}, 
        {t: 110, count: 4, type: 'z_normal'},
        {t: 100, count: 2, type: 'z_bucket'}, 
        // 增加密度
        {t: 80, count: 6, type: 'z_normal'},
        {t: 70, count: 4, type: 'z_bucket'},
        {t: 45, count: 10, type: 'z_normal'}, 
        {t: 40, count: 5, type: 'z_bucket'},
        {t: 20, count: 12, type: 'z_normal'}, 
        {t: 15, count: 6, type: 'z_bucket'}
    ];

    setInterval(() => {
        if(!state.running) return;
        let waves = waveSchedule.filter(w => w.t === state.time);
        waves.forEach(w => {
            for(let i=0; i<w.count; i++) {
                setTimeout(() => spawnZombie(w.type), i * 800);
            }
        });
        // 随机生成频率增加
        if(Math.random() < 0.03) spawnZombie('z_normal');
    }, 1000);
}

function updateUI() {
    document.getElementById('lingzhi-val').innerText = state.lingzhi;
    document.querySelectorAll('.card').forEach(c => {
        let type = c.dataset.type;
        let cost = UNIT_STATS[type].cost;
        if(state.lingzhi < cost) c.classList.add('disabled');
        else c.classList.remove('disabled');
    });
}

function showFloatText(el, text, color) {
    let div = document.createElement('div');
    div.innerText = text;
    div.className = 'float-text';
    div.style.color = color;
    div.style.left = '20px';
    div.style.top = '0px';
    el.appendChild(div);
    setTimeout(() => div.remove(), 1000);
}

function showTip(e, type) {
    let info = UNIT_STATS[type];
    if(!info) return;
    tipEl.style.display = 'block';

    // 智能定位，防止tip超出屏幕
    let x = e.clientX + 15;
    let y = e.clientY + 15;
    if(x + 200 > window.innerWidth) x -= 220;

    tipEl.style.left = x + 'px';
    tipEl.style.top = y + 'px';
    tipEl.innerHTML = `<div class="tt-title">${info.name}</div><div class="tt-stat">${info.text}</div>`;
}

function hideTip() { tipEl.style.display = 'none'; }

function gameOver(win) {
    state.running = false;
    if(win) document.getElementById('win-modal').style.display = 'flex';
    else document.getElementById('lose-modal').style.display = 'flex';
}

function sendResult(resultType) {
    let replyContent = resultType === 'win' 
        ? "试炼成功，灵知+1，博闻+1，术动+1，器韵+1，观势+1" 
        : "试炼失败，灵知-1，博闻-1，术动-1，器韵-1，观势-1";

    let command = `/send {{user}} ${replyContent}`;

    if (typeof triggerSlash === 'function') {
        triggerSlash(command);
    } else {
        console.log(command);
    }
    document.querySelectorAll('.modal-overlay').style.display = 'none';
}

init();

</script>
</body>
</html>
