    <style>
        /* --- 基础样式与字体 --- */
        @font-face { font-family: 'HorrorFont'; src: url('https://files.catbox.moe/ol73fi.otf'); }
        @font-face { font-family: 'PaperFont'; src: url('https://files.catbox.moe/8gy4hn.otf'); }

        :root { --blood: #8a0000; --gold: #c0a060; }

        body {
            margin: 0; padding: 0; width: 100%; min-height: 100vh;
            background-color: #050505; color: #dcdcdc;
            display: flex; justify-content: center; align-items: flex-start;
            font-family: 'HorrorFont', serif; overflow-x: hidden;
        }

        /* 响应式容器 */
        .game-container {
            position: relative; width: 100%; max-width: 800px; min-height: 600px;
            background-color: #050505; border: 2px solid #2a2a2a;
            box-shadow: 0 0 30px #000; margin: 10px; padding-bottom: 20px;
        }

        .scene { display: none; padding: 15px; box-sizing: border-box; width: 100%; }
        .active-scene { display: block; animation: fadeIn 0.5s forwards; }

        @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }

        /* --- UI 交互元素 --- */
        .menu-btn {
            padding: 12px; border: 1px solid #4a0000; background: rgba(20,0,0,0.8);
            color: #dcdcdc; font-family: 'HorrorFont'; font-size: 1.1em; cursor: pointer;
            transition: 0.3s; text-align: center; margin: 5px 0; width: 100%;
        }
        .menu-btn:hover { background: var(--blood); color: #000; }

        .back-btn { background: #222; border-color: #444; margin-top: 10px; }

        .config-item { margin-bottom: 20px; border-left: 3px solid var(--blood); padding-left: 10px; }
        .config-label { color: var(--blood); font-size: 1.2em; margin-bottom: 10px; }

        .option-grid { 
            display: grid; 
            grid-template-columns: repeat(auto-fill, minmax(90px, 1fr)); 
            gap: 8px; margin-bottom: 8px;
        }

        .opt-btn {
            background: #111; border: 1px solid #333; color: #aaa; padding: 8px 4px;
            font-size: 0.9em; cursor: pointer; transition: 0.2s;
        }
        .opt-btn.selected { border-color: var(--blood); color: #fff; background: #200; }

        /* 自定义输入框 */
        .custom-input {
            width: 100%; background: #000; border: 1px solid #444;
            color: var(--gold); padding: 8px; box-sizing: border-box;
            font-family: 'PaperFont'; font-size: 0.9em; margin-top: 5px;
        }

        /* --- 档案纸张（自适应） --- */
        #scene-exam-info {
            background: url('https://files.catbox.moe/q5egct.JPG') center top repeat;
            background-size: cover; font-family: 'PaperFont', serif; color: #000;
            min-height: 500px;
        }
        .info-paper { position: relative; width: 100%; height: 100%; }
        .info-title { text-align: center; font-size: 1.8em; font-weight: 900; color: var(--blood); margin-bottom: 20px; }
        .info-line { border-bottom: 1px solid #666; margin: 12px 0; font-size: 1.1em; padding-bottom: 4px; word-break: break-all; }
        .stamp {
            position: absolute; right: 10px; bottom: 60px; width: 80px; height: 80px;
