            display: flex; justify-content: center; align-items: center;
            font-weight: bold; transform: rotate(-15deg); opacity: 0.7; pointer-events: none;
        }

        /* 隐藏爆闪层 */
        #flash-overlay {
            position: fixed; inset: 0; background: #000; z-index: 999;
            display: none; justify-content: center; align-items: center;
        }
    </style>
</head>
<body>

    <div id="flash-overlay"><img id="flash-img" style="max-width: 80%; filter: drop-shadow(0 0 20px red);"></div>

    <div class="game-container">
        <div id="scene-start" class="scene active-scene">
            <div style="text-align:center; padding-top:100px;">
                <h1 style="color:var(--blood); font-size:2.8em; text-shadow:0 0 10px #f00;">试炼考试中心</h1>
                <div style="max-width:300px; margin: 0 auto; padding-top:50px;">
                    <button class="menu-btn" onclick="goConfig('single')">单 人 试 炼</button>
                    <button class="menu-btn" onclick="goConfig('multi')">多 人 试 炼</button>
                </div>
            </div>
        </div>

        <div id="scene-config" class="scene">
            <div style="text-align:center; color:var(--blood); font-size:1.5em; margin-bottom:15px;">试炼参数修正</div>

            <div class="config-item">
                <div class="config-label">1. 考生背景身份</div>
                <div class="option-grid" id="grid-id"></div>
                <input type="text" class="custom-input" id="custom-id" placeholder="可在此自定义身份描述...">
            </div>

            <div class="config-item">
                <div class="config-label">2. 试炼地理场域</div>
                <div class="option-grid" id="grid-bg"></div>
                <input type="text" class="custom-input" id="custom-bg" placeholder="可在此自定义场景描述...">
            </div>

            <div class="config-item">
                <div class="config-label">3. 威胁/难度等级</div>
                <div class="option-grid" id="grid-lv"></div>
                <input type="text" class="custom-input" id="custom-lv" placeholder="可在此自定义难度等级...">
            </div>

            <div class="config-item" id="partner-box" style="display:none;">
                <div class="config-label">4. 伴考灵修人员</div>
                <div class="option-grid" id="grid-pt"></div>
                <input type="text" class="custom-input" id="custom-pt" placeholder="可在此自定义伙伴...">
            </div>

            <button class="menu-btn" style="background:#400;" onclick="generateDoc()">核准档案</button>
            <button class="menu-btn back-btn" onclick="resetAll()">返回初始界面</button>
        </div>

        <div id="scene-exam-info" class="scene">
            <div class="info-paper">
                <div class="info-title">试 炼 考 试 准 考 证</div>
                <div id="info-display"></div>
                <div class="stamp">核准入场</div>
                <button class="menu-btn" style="margin-top:40px; border-color:#000; color:#000; background:none;" onclick="startST()">确认入考场</button>
                <button class="menu-btn back-btn" onclick="switchScene('scene-config')">重新修正</button>
            </div>
        </div>
    </div>

    <script>
        const worldData = {
            ids: ["XX系学生", "系内教师", "玄学世家", "民俗学者", "游魂灵体", "名门千金", "民间传人", "随机"],
            bgs: ["寂川私校", "阴森旅馆", "废弃医院", "荒村祠堂", "夺命地铁", "古宅旧址", "午夜戏台", "随机"],
            lvs: ["C(无害)", "B(游魂)", "A(怨灵)", "S(邪祟)", "SS(妖异)", "SSS(极凶)", "随机"],
            pts: [
                {n:"明离书", i:"https://files.catbox.moe/scs8hd.PNG"},
                {n:"钱兜兜", i:"https://files.catbox.moe/x2j37a.PNG"},
                {n:"聆声", i:"https://files.catbox.moe/pavagi.PNG"},
                {n:"刃", i:"https://files.catbox.moe/j399qe.PNG"},
                {n:"随机", i:"https://files.catbox.moe/o1mwtc.PNG"}
            ]
        };

        let current = { mode:'single', id:'', bg:'', lv:'', pt:'' };

        function initUI(gridId, list, key) {
            const grid = document.getElementById(gridId);
            grid.innerHTML = '';
            list.forEach(item => {
                const val = typeof item === 'string' ? item : item.n;
                const btn = document.createElement('button');
                btn.className = 'opt-btn';
                if(val === '随机') btn.classList.add('selected');
                btn.innerText = val;
                btn.onclick = () => {
                    grid.querySelectorAll('.opt-btn').forEach(b => b.classList.remove('selected'));
                    btn.classList.add('selected');
                    current[key] = val;
                };
                grid.appendChild(btn);
            });
            current[key] = '随机'; // 默认随机
        }

        function goConfig(mode) {
            current.mode = mode;
            document.getElementById('partner-box').style.display = (mode === 'multi') ? 'block' : 'none';
            initUI('grid-id', worldData.ids, 'id');
            initUI('grid-bg', worldData.bgs, 'bg');
            initUI('grid-lv', worldData.lvs, 'lv');
            if(mode === 'multi') initUI('grid-pt', worldData.pts, 'pt');
            switchScene('scene-config');
        }

        function generateDoc() {
            // 优先读取自定义文本
            const cId = document.getElementById('custom-id').value;
            const cBg = document.getElementById('custom-bg').value;
            const cLv = document.getElementById('custom-lv').value;
            const cPt = document.getElementById('custom-pt').value;

            const final = {
                id: cId || (current.id === '随机' ? worldData.ids[Math.floor(Math.random()*7)] : current.id),
                bg: cBg || (current.bg === '随机' ? worldData.bgs[Math.floor(Math.random()*7)] : current.bg),
                lv: cLv || (current.lv === '随机' ? worldData.lvs[Math.floor(Math.random()*6)] : current.lv),
                pt: current.mode === 'single' ? '孤身一人' : (cPt || (current.pt === '随机' ? worldData.pts[Math.floor(Math.random()*4)].n : current.pt))
            };

            current.final = final;

            // 爆闪与转场
            const fLayer = document.getElementById('flash-overlay');
            const fImg = document.getElementById('flash-img');
            fImg.src = current.mode === 'multi' ? (worldData.pts.find(x=>x.n===final.pt)?.i || worldData.pts[0].i) : "https://files.catbox.moe/o1mwtc.PNG";

            fLayer.style.display = 'flex';
            setTimeout(() => {
                fLayer.style.display = 'none';
                renderPaper(final);
                switchScene('scene-exam-info');
            }, 600);
        }

        function renderPaper(f) {
            document.getElementById('info-display').innerHTML = `
                <div class="info-line"><b>姓名：</b> {{user}}</div>
                <div class="info-line"><b>试炼背景：</b> ${f.id}</div>
                <div class="info-line"><b>考场场域：</b> ${f.bg}</div>
                <div class="info-line"><b>危险系数：</b> <span style="color:var(--blood)">${f.lv}</span></div>
                <div class="info-line"><b>同行伴考：</b> ${f.pt}</div>
            `;
        }

        function switchScene(id) {
            document.querySelectorAll('.scene').forEach(s => s.classList.remove('active-scene'));
            document.getElementById(id).classList.add('active-scene');
            window.scrollTo(0, 0);
        }

        function resetAll() {
            document.querySelectorAll('.custom-input').forEach(i => i.value = '');
            switchScene('scene-start');
        }

        function startST() {
            const f = current.final;
            const cmd = `/send {{user}}进入了试炼考试，考生身份背景为【${f.id}】，试炼场域设定在【${f.bg}】，危险等级判定为【${f.lv}】，同行伴考人员：【${f.pt}】。|/trigger`;

            if (typeof triggerSlash === 'function') {
                triggerSlash(cmd);
            } else {
                console.log("模拟指令发送: " + cmd);
            }
            alert("因果已成，试炼开始。");
        }
    </script>
</body>
</html>
